import {createSelector} from 'reselect'

const selectUser = state => state.userReducer;

// selectUser = {
    // userReducer:{
    //     currentUser:..
    // }
//}

console.log('select user from selector', selectUser())

export const selectCurrentUser = createSelector(
    selectUser,
    (user) => user.currentUser
)
