import React, { Component } from 'react';
import { StyledWrapper, FormTitle, FormSubTitle, SignInForm, ButtonsWrapper } from './styles';
import Input from '../formInput/formInput';
import CustomButton from '../customButton/customButton';

import { googleSignInStart, emailSignInStart } from '../../redux/user/userActions';
import { connect } from 'react-redux';
import { useState } from 'react';

const SignIn = (props) => {
	// constructor(){
	// 	super();
	// 	this.state = {
	// 		email:'',
	// 		password:''
	// 	}
	// }

	const [ userCredentials, setUserCredentials ] = useState({ email: '', password: '' });

	const handleSubmit = async (e) => {
		e.preventDefault();
		const { email, password } = userCredentials;
		const { emailSignInStart } = props;
		emailSignInStart(email, password);
	};

	const handleChange = (e) => {
		setUserCredentials({
			[e.target.name]: e.target.value
		});
	};

	const { googleSignInStart } = this.props;
	return (
		<StyledWrapper>
			<FormTitle>I already have an account</FormTitle>
			<FormSubTitle>Sign in with your email and password</FormSubTitle>

			<SignInForm onSubmit={handleSubmit}>
				<Input
					name="email"
					type="email"
					value={userCredentials.email}
					handleChange={handleChange}
					label="email"
				/>
				<Input
					name="password"
					type="password"
					wider
					value={userCredentials.password}
					handleChange={handleChange}
					label="password"
				/>
				<ButtonsWrapper>
					<CustomButton type="submit">Sign In</CustomButton>
					<CustomButton type="button" onClick={googleSignInStart} isGoogleSignIn={true}>
						Sign In With Google
					</CustomButton>
				</ButtonsWrapper>
			</SignInForm>
		</StyledWrapper>
	);
};

const mapDispatchToProps = (dispatch) => ({
	googleSignInStart: () => dispatch(googleSignInStart()),
	emailSignInStart: (email, password) => dispatch(emailSignInStart({ email, password }))
});

export default connect(null, mapDispatchToProps)(SignIn);
