import React from 'react';
import { StyledWrapper, ItemCount, ShoppingIcon } from './styles.js';
import { connect } from 'react-redux';
import { setVisibilityAction } from '../../redux/cart/cartActions.js';
import {selectCountedItems} from '../../redux/cart/cart.selectors'
import {createStructuredSelector} from 'reselect'

const CartIcon = ({ setCartVisible, itemsCounted }) => {
	return (
		<StyledWrapper onClick={() => setCartVisible()}>
			<ShoppingIcon />
			<ItemCount>{itemsCounted}</ItemCount>
		</StyledWrapper>
	);
};

const mapDispatchToProps = (dispatch) => {
	return {
		setCartVisible: () => dispatch(setVisibilityAction())
	};
};

// const mapStateToProps = createStructuredSelector({
// 		itemsCounted: selectCountedItems
// });

export default connect(null, mapDispatchToProps)(CartIcon);
