import React from 'react'
import {StyledWrapper,ItemCount,ShoppingIcon} from './styles.js'
import {connect} from 'react-redux';
import {setVisibilityAction} from '../../redux/cart/cartActions.js'

const CartIcon = ({setCartVisible,itemsCounted}) => {

  // let sumOfItems = items.reduce((acc,elem) => {
  //   return acc + elem.quantity
  // },0)


  return (
    <StyledWrapper onClick={() => setCartVisible()}>
      <ShoppingIcon />
      <ItemCount>{itemsCounted}</ItemCount>
    </StyledWrapper>
  )
}

const mapDispatchToProps = (dispatch) => {
  return{
    setCartVisible:() => dispatch(setVisibilityAction())
  }
}


const mapStateToProps = (state) => {
  return {
    itemsCounted: state.cartReducer.cartItems.reduce((acc,elem) => (acc + elem.quantity),0)
  }
} 

export default connect(mapStateToProps,mapDispatchToProps)(CartIcon);
