import React from 'react';
import CollectionOverview from '../../components/collectionsOverview/collectionsOverview';
import { Route, Switch } from 'react-router-dom';
import CategoryPage from '../collection/collection';
import NoMatch404 from '../../components/404/404'
import {firestore,convertCollectionsSnapshotToMap} from '../../firebase/firebase.utils';
import {connect} from 'react-redux'
import {getCollectionsDataFromDbAction} from '../../redux/shop/shopActions'
import WithSpinner from '../../components/withSpinner/withSpinner'


const CollectionOverviewWithSpinner = WithSpinner(CollectionOverview);
const CategoryPageWithSpinner = WithSpinner(CategoryPage)

class ShopPage extends React.Component {

	state = {
		loading:true
	}

	unsubscribeFromSnapshot = null;

	componentDidMount(){
		const {setCollection} = this.props;
		const collectionRef = firestore.collection('shop');
		collectionRef.onSnapshot(async (snapshot) => {
			const collectionsFromDb = convertCollectionsSnapshotToMap(snapshot);
			setCollection(collectionsFromDb)
			this.setState({loading:false})
		})
	}

    render() {
        const {match} = this.props;
		const {loading} = this.state;
        return (
            <div>
                <Switch>
                    <Route exact path={`${match.path}`} render={(props) => <CollectionOverviewWithSpinner loading={loading} {...props}/>} />
                    <Route path={`${match.path}/:collectionId`} component={CategoryPage} />
                    {/* <Route>
                        <NoMatch404/>
                    </Route> */}
                </Switch>
            </div>
        );
    }
}


const mapDispatchToProps = (dispatch) => ({
	setCollection:(data) => dispatch(getCollectionsDataFromDbAction(data))
})


export default connect(null,mapDispatchToProps)(ShopPage);
