import actionTypes from './cartActionTypes'



export const setVisibilityAction = () => ({
    type:actionTypes.TOGGLE_CART_HIDDEN,
})

export const addItemAction = (item) => ({
    type:actionTypes.ADD_ITEM_TO_CART,
    payload:item
})
