import actionTypes from './cartActionTypes'
const initialState = {
    hidden:true
}


const cartReducer = (state=initialState,action) => {
    switch (action.type) {
        case actionTypes.TOGGLE_CART_HIDDEN:
             return{
                 hidden:!state.hidden,
                 ...state
             }
        default:
            return state;
    }
}


export default cartReducer;
