import styled from 'styled-components';
import CollectionItemContainer from '../../components/collectionItem/collectionItemContainer.jsx';

export const StyledWrapper = styled.div`
	display: flex;
	flex-direction: column;
`;

export const CategoryTitle = styled.h2`
	font-size: 38px;
	margin: 0 auto 30px;
`;

export const CollectionItems = styled.div`
	display: grid;
	grid-template-columns: 1fr 1fr 1fr 1fr;
	grid-gap: 10px;
`;

export const CollectionItemStyled = styled(CollectionItemContainer)`
  margin-bottom: 30px;
`;
