import styled from 'styled-components';

export const HomePageContainer = styled.div`
	display: flex;
	align-items: center;
	padding: 20px 80px;
`;

export const DirectoryMenu = styled.div`
	width: 100%;
	display: flex;
	flex-flow: row wrap;
	margin-left: -8px;
	width: 100%;
`;

export const MenuItem = styled.div`
	min-width: 150px;
	height: 250px;
	flex: auto;
	margin: 0 8px 8px 0;
	display: flex;
	align-items: center;
	justify-content: space-around;
    border: 1px solid black;
    &:nth-child(4n + 1) {
		width: 300px;
	}
	&:nth-child(4n + 1):nth-child(4n + 2) {
		width: 325px;
	}
	&:nth-child(4n + 1):nth-child(4n + 3) {
		width: 400px;
	}
	&:nth-child(4n + 1):nth-child(4n + 4) {
		width: 380px;
	}
`;

export const Content = styled.div`
	/* height: 90px;
	padding: 0 25px;
	display: flex;
	justify-content: center;
	align-items: center;
	flex-direction: column;
	border: 1px solid black; */

`;

export const Title = styled.h1`
	font-weight: bold;
	margin-bottom: 6px;
	font-size: 22px;
	color: #4a4a4a;
`;

export const SubTitle = styled.h2`
	font-weight: lighter;
	font-size: 16px;
`;
