import {createSelector} from 'reselect'

const selectUser = state => state.userReducer;

// selectUser = {
    // userReducer:{
    //     currentUser:..
    // }
//}


export const selectCurrentUser = createSelector(
    selectUser,
    (user) => user.currentUser
)



const exampleState = {
    shop:{
        taxPercent:9,
        items:[
            {name:'apple',value:1.20},
            {name:'orange', value:2.65}
        ]
    },
    magazin:{
        taxPercent:233,
        items:[
            {name:'łódź',value:300},
            {name:'grabie', value:5000}
        ]
    },

}

const selectShop = state => state.shop
const selectMagazin = state => state.magazin



// console.log(selectMagazin(exampleState))


const selectShopItems = createSelector(
    selectShop,
    shop => shop.items
)

console.log(selectShopItems(exampleState))
