//react
import React,{Component} from 'react';

//styles
import GlobalStyle from './globalStyles';

//router
import {Route,Switch,Redirect} from 'react-router-dom'

//firebase
import {auth,createUserProfileDocument} from './firebase/firebase.utils';

//redux
import {connect} from 'react-redux'
import {setCurrentUserAction} from './redux/user/userActions';
import {selectCurrentUser} from './redux/user/user.selectors';

// reselect
import {createStructuredSelector} from 'reselect'

// pages
import SignInSignOut  from "./pages/signin-signout/signin-signout";
import ShopPage from './pages/shoppage/shoppage';
import HomePage from './pages/homepage/homepage'
import CheckoutPage from './pages/checkout/checkoutPage';

// components
import HeaderContainer from './components/header/headerContainer';


class App extends Component{

  unsubscribeFromAuth = null;

  // store authenticate bu google user to our state
  componentDidMount(){
    // eslint-disable-next-line react-hooks/exhaustive-deps
    // auth library -> userAuth tells what user is in our auth

    const {setCurrentUser} = this.props;

    this.unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth => {
      if(userAuth){
        const userRef = await createUserProfileDocument(userAuth);
        userRef.onSnapshot( snapshot => {
          // const userFromSnapshot =  snapshot.data();
          setCurrentUser({
              ...snapshot.data(),
              id:snapshot.id
            }
          )
        })
      }else{
        //return null
        setCurrentUser(null)
      }
      // addCollectionAndDocs('shop',this.props.shopItemsArray.map(({title,items}) => ({title,items})))

  })
    }

    // check if log in or log out for not get memory leaks
    componentWillUnmount(){
      this.unsubscribeFromAuth()
    }

    render(){
      return (
        <>
          <GlobalStyle/>
          <HeaderContainer/>
          <Switch>
            <Route path="/" exact component={HomePage}/>
            <Route path="/sklep" component={ShopPage} />
            <Route path="/checkout" exact component={CheckoutPage}/>
            {!this.props.currentUser ? <Route path="/signin" component={SignInSignOut}/> : <Redirect to="/"/> }
          </Switch>
        </>
      );
    }

}


const mapDispatchToProps = (dispatch) => {
  return {
    setCurrentUser: user => dispatch(setCurrentUserAction(user))
  }
} 

const mapStateToProps = createStructuredSelector({
  currentUser:selectCurrentUser,
  // shopItemsArray:selectOverviewItems

})


export default connect(mapStateToProps,mapDispatchToProps)(App);
