import React, {Component} from 'react';
import { StyledWrapper,FormTitle,FormSubTitle,SignInForm,ButtonsWrapper } from './styles';
import Input from '../formInput/formInput'
import CustomButton from '../customButton/customButton'

import {signInWithGoogle,auth} from '../../firebase/firebase.utils'
import {googleSignInStart} from '../../redux/user/userActions'
import {connect} from 'react-redux'

class SignIn extends Component{
	constructor(){
		super();
		this.state = {
			email:'',
			password:''
		}
	}

	handleSubmit = async (e) => {
		e.preventDefault();
	};

	handleChange = (e) => {
		this.setState({
			[e.target.name]: e.target.value
		});
	};

	render(){
	const {googleSignInStart} = this.props;
	return (
		<StyledWrapper>
			<FormTitle>I already have an account</FormTitle>
			<FormSubTitle>Sign in with your email and password</FormSubTitle>

			<SignInForm onSubmit={this.handleSubmit}>
        <Input 
          name="email" 
          type="email" 
          value={this.state.email} 
          handleChange={this.handleChange} 
          label="email"/>
        <Input 
          name="password" 
          type="password" 
          wider 
          value={this.state.password} 
          handleChange={this.handleChange} 
          label="password"/>
        <ButtonsWrapper>
          <CustomButton type="submit">Sign In</CustomButton>
          <CustomButton type="button" onClick={googleSignInStart} isGoogleSignIn={true}>Sign In With Google</CustomButton>
        </ButtonsWrapper>
			</SignInForm>
		</StyledWrapper>
	);
	}

};


const mapDispatchToProps = (dispatch) => ({
	googleSignInStart: () => dispatch(googleSignInStart())
})

export default connect(null,mapDispatchToProps)(SignIn);
