import React from 'react';
import {HeaderLogo,StyledWrapper,LogoLink,Options,OptionsLink,SignOutLink} from './styles'
import {connect} from 'react-redux';

import {auth} from '../../firebase/firebase.utils'
import CartIcon from '../cartIcon/cartIcon';
import CartDropdown from '../cartDropdown/cartDropdown';
import {selectHidden} from '../../redux/cart/cart.selectors'
import {selectCurrentUser} from '../../redux/user/user.selectors'

const Header = ({currentUser,hidden}) => {
  return (
    <StyledWrapper>
        <LogoLink to="/">
            <HeaderLogo />
        </LogoLink>
        <Options>
            <OptionsLink to="/sklep">
                SKLEP
            </OptionsLink>
            <OptionsLink to="/kontakt">
                KONTAKT
            </OptionsLink>
            {
                currentUser 
                ? 
                <SignOutLink onClick={() => auth.signOut()}>SIGN OUT</SignOutLink>
                :
                <OptionsLink to="/signin">
                    SIGN IN
                </OptionsLink>
            }
            <CartIcon/>
        </Options>
        {hidden ? null : <CartDropdown/> }

    </StyledWrapper>
  )
}


const mapStateToProps = (state) => {
    return {
        currentUser:selectCurrentUser(state),
        hidden:selectHidden(state)
    }
}


export default connect(mapStateToProps)(Header)
