import {GET_SHOP_DATA_FROM_DB } from './shopActionTypes'

const INITITAL_STATE = {
	collections: {}
};

const shopReducer = (state = INITITAL_STATE, action) => {
	switch (action.type) {
		case GET_SHOP_DATA_FROM_DB:
			return{
				...state,
				collections:action.payload
			}
		default:
			return state;
	}
};

export default shopReducer;
