import React from 'react';
import {HeaderLogo,StyledWrapper,LogoLink,Options,OptionsLink,SignOutLink} from './styles'
import {connect} from 'react-redux';

import {auth} from '../../firebase/firebase.utils'

const Header = ({currentUser}) => {
  return (
    <StyledWrapper>
        <LogoLink to="/">
            <HeaderLogo />
        </LogoLink>
        <Options>
            <OptionsLink to="/sklep">
                SKLEP
            </OptionsLink>
            <OptionsLink to="/kontakt">
                KONTAKT
            </OptionsLink>
            {
                currentUser 
                ? 
                <SignOutLink onClick={() => auth.signOut()}>SIGN OUT</SignOutLink>
                :
                <OptionsLink to="/signin">
                    SIGN IN
                </OptionsLink>
            }
        </Options>
    </StyledWrapper>
  )
}


const mapStateToProps = (state) => {
    return {
        currentUser:state.userReducer.currentUser
    }
}

export default connect(mapStateToProps)(Header)
