import { StyledWrapper, CartItems,MessageForEmpty } from './styles';
import React from 'react';
import CustomButton from '../customButton/customButton';
import { connect } from 'react-redux';
import CartItem from '../cartItem/cartItem';
import {selectCartItems,selectHidden} from '../../redux/cart/cart.selectors'
import {createStructuredSelector} from 'reselect'
import {withRouter} from 'react-router-dom';
import {setVisibilityAction} from '../../redux/cart/cartActions'

const CartDropdown = ({ checkoutItems,history,toggleHidden }) => {
	const customButtonHandler = () => {
		history.push('/checkout');
		toggleHidden();
	}
	return (
		<StyledWrapper>
			<CartItems>
			{checkoutItems.length ? checkoutItems.map((item) => 
				<CartItem item={item} key={item.id} />) : <MessageForEmpty> Koszyk jest pusty</MessageForEmpty>
			}
            </CartItems>
			<CustomButton onClick={() => customButtonHandler()}>GO TO CHECKOUT</CustomButton>
		</StyledWrapper>
	);
};

const mapStateToProps = createStructuredSelector({
	checkoutItems: selectCartItems,
	hidden:selectHidden
});


const mapDispatchToProps = (dispatch) => ({
	toggleHidden: () => dispatch(setVisibilityAction()) 
})

export default withRouter(connect(mapStateToProps,mapDispatchToProps)(CartDropdown));
