import actionTypes from './cartActionTypes'
import {addItemToCart} from './cartUtils'
const initialState = {
    hidden:true,
    cartItems:[]
}


// const obj = [
//     {
//         name:'Brown Brim',
//         price:25,
//         numberOf:0
//     }
// ]

const cartReducer = (state=initialState,action) => {
    switch (action.type) {
        case actionTypes.TOGGLE_CART_HIDDEN:
             return{
                ...state,
                 hidden:!state.hidden
             }
        case actionTypes.ADD_ITEM_TO_CART:
            return{
                ...state,
                cartItems:[...state.cartItems,addItemToCart(state.cartItems,action.payload)]
            }
        default:
            return state;
    }
}


export default cartReducer;
