import styled from 'styled-components';

export const HomePageContainer = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	padding: 20px 80px;
`;

export const DirectoryMenu = styled.div`
	width: 100%;
	display: flex;
	flex-wrap: wrap;
	justify-content: space-between;
`;

export const MenuItem = styled.div`
    min-width:30%;
    height:240px;
    flex:1 1 auto;
    display:flex;
    align-items:center;
    justify-content:center;
    border:1px solid black;
    &:first-child{
        margin-right:7.5px;
    }
    &:last-child{
        margin-left:7.5px;
    }
`;

export const Content = styled.div`
    height:90px;
    padding:0 25px;
    display:flex;
    justify-content:center;
    align-items:center;
    flex-direction:column;
    border:1px solid black;
`;

export const Title = styled.h1`
    font-weight:bold;
    margin-bottom:6px;
    font-size:22px;
    color:#4a4a4a;
`;

export const SubTitle = styled.h2`
    font-weight:lighter;
    font-size:16px;
`;
