export const addItemToCart = (cartItems,cartItemToAdd) => {
    if(cartItems.includes(cartItemToAdd)){
       return {
           ...cartItems,
           cartItemToAdd:{
               ...cartItemToAdd,
               quantity:cartItemToAdd.quantity +=1
           }
       }
    }else{
        return cartItemToAdd
    }
}
