import React from 'react';
import { StyledWrapper, CategoryTitle,CollectionItems,CollectionItemStyled } from './styles';
import { connect } from 'react-redux';
import { selectCollection,selectItems } from '../../redux/shop/shop.selectors';
import WithSpinner from '../../components/withSpinner/withSpinner'


const WrapperWithSpinner = WithSpinner(StyledWrapper)


const CollectionPage = (props) => {
	//   const { title, items } = collection;
	return (
		<WrapperWithSpinner loading={props.mainState.isFetching}>
			 <CategoryTitle>{props.title}</CategoryTitle> 
      { <CollectionItems>
          {props.items.map((item) => <CollectionItemStyled item={item} key={item.id} />)}
      </CollectionItems> }
		</WrapperWithSpinner>
	);
};

const mapStateToProps = (state, ownProps) => ({
	collection: selectCollection(ownProps.match.params.collectionId)(state),
	mainState:state.shopReducer
});

export default connect(mapStateToProps)(CollectionPage);
