import {call,all} from 'redux-saga/effects'
import {onfetchCollectionsStartSaga} from './shop/shopSagas'
import {userSagas} from './user/userSagas'

function* rootSaga() {
    yield all(
        [
            call(onfetchCollectionsStartSaga),
            call(userSagas)
        ]
    )
}


export default rootSaga;
