import React from 'react';
import {
	StyledWrapper,
	CheckoutImageContainer,
	CheckoutImage,
	CheckoutName,
	CheckoutRemove,
	CheckoutQuantity,
	CheckoutPrice,
	CheckoutArrow,
	CheckoutValue
} from './styles';
import { connect } from 'react-redux';
import { removeItemAction,addItemAction,decreaseItemAction } from '../../redux/cart/cartActions';

const CheckoutItem = ({ item, removeItem,addItemQuantity,removeItemQuantity }) => {
	const { imageUrl, name, quantity, price } = item;
	return (
		<StyledWrapper>
			<CheckoutImageContainer>
				<CheckoutImage alt="item" src={imageUrl} />
			</CheckoutImageContainer>
			<CheckoutName>{name}</CheckoutName>
			<CheckoutQuantity>
				<CheckoutArrow onClick={() => removeItemQuantity(item)}>&#10094;</CheckoutArrow>
				<CheckoutValue>{(quantity < 1) ? removeItem(item) : quantity}</CheckoutValue>
				<CheckoutArrow onClick={()=> addItemQuantity(item) }>&#10095;</CheckoutArrow>
			</CheckoutQuantity>
			<CheckoutPrice>{price * quantity} z≈Ç</CheckoutPrice>
			<CheckoutRemove onClick={() => removeItem(item)}>&#10005;</CheckoutRemove>
		</StyledWrapper>
	);
};

const mapDispatchToProps = (dispatch) => ({
	removeItem: (elem) => dispatch(removeItemAction(elem)),
	addItemQuantity: (elem) => dispatch(addItemAction(elem)),
	removeItemQuantity: (elem) => dispatch(decreaseItemAction(elem))
});

export default connect(null, mapDispatchToProps)(CheckoutItem);
