import { createSelector } from 'reselect';

const selectItems = (state) => state.shopReducer;

export const selectShopItems = createSelector([ selectItems ], (items) => items.collections);

export const selectCollection = (collectionUrlParam) =>
	createSelector([ selectShopItems ], (collections) => collections[collectionUrlParam]);

export const selectOverviewItems = createSelector([ selectShopItems ], (items) =>
	Object.keys(items).map((key) => items[key])
);
