import React from 'react';
import FormInput from '../formInput/formInput';
import CustomButton from '../customButton/customButton';
import { StyledWrapper, Title, Subtitle } from './styles';
import {connect} from 'react-redux'
import {signUpStart} from '../../redux/user/userActions'
import {useState, useEffect} from 'react'

import { auth, createUserProfileDocument } from '../../firebase/firebase.utils';

const SignUp = () => {
	// constructor() {
	// 	super();
	// 	this.state = {
	// 		displayName: '',
	// 		email: '',
	// 		password: '',
	// 		confirmPassword: ''
	// 	};
    // }

    const [userCredentials,setUserCredentials] = useState({
            displayName: '',
			email: '',
			password: '',
			confirmPassword: ''
    })

    const { displayName, email, password, confirmPassword } = userCredentials;


	const handleSubmit = async (e) => {
        e.preventDefault();
        const { displayName, email, password, confirmPassword } = this.state;
        const {signUpWithEmail} = this.props;

        if(password !== confirmPassword){
            alert(`password don't match`)
            return;
        }

        signUpWithEmail({
            displayName,
            email,
            password
        })
    };

	const handleChange = (e) => {
        setUserCredentials({
            ...userCredentials,
            [e.target.name]:e.target.value

        })
    };

		return (
			<StyledWrapper>
				<Title>I do not have a account</Title>
				<Subtitle>Sign up with your email and password</Subtitle>
				<form onSubmit={this.handleSubmit}>
                    <FormInput 
                    type="text" 
                    name="displayName" 
                    value={displayName} 
                    onChange={this.handleChange}
                    label="Display Name"
                    required 
                    />
                     <FormInput 
                    type="email" 
                    name="email" 
                    value={email} 
                    onChange={this.handleChange}
                    label="Email"
                    required 
                    />
                     <FormInput 
                    type="password" 
                    name="password" 
                    value={password} 
                    onChange={this.handleChange} 
                    label="Password"
                    required
                    />
                     <FormInput 
                    type="password" 
                    name="confirmPassword" 
                    value={confirmPassword} 
                    onChange={this.handleChange} 
                    label="Confirm Password"
                    required
                    />
                    <CustomButton type="submit">Sign Up</CustomButton>
				</form>
			</StyledWrapper>
		);

}


const mapDispatchToProps = (dispatch) => ({
    signUpWithEmail: (user) => dispatch(signUpStart(user))
})

export default connect(null,mapDispatchToProps)(SignUp);
