import React from 'react';
import CollectionOverviewContainer from '../../components/collectionsOverview/collectionOverviewContainer'
import { Route, Switch } from 'react-router-dom';
import CollectionPage from '../collection/collection';
import NoMatch404 from '../../components/404/404'
import {connect} from 'react-redux'
import {fetchCollectionsStartAsync} from '../../redux/shop/shopActions'
import WithSpinner from '../../components/withSpinner/withSpinner'
import {selectCollectionFetching,selectIsCollectionLoaded} from '../../redux/shop/shop.selectors'
import {createStructuredSelector} from 'reselect'
import CollectionContainer from '../collection/collectionContainer'


// const CollectionOverviewWithSpinner = WithSpinner(CollectionOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage)

class ShopPage extends React.Component {

	componentDidMount(){
		const {setCollection} = this.props;
		setCollection();
	}

    render() {
        const {match,isCollectionLoaded} = this.props;
        return (
            <div>
                <Switch>
                    <Route exact path={`${match.path}`} component={CollectionOverviewContainer} />
                    <Route path={`${match.path}/:collectionId`} component={CollectionContainer} />
                </Switch>
            </div>
        );
    }
}

const mapDispatchToProps = (dispatch) => ({
	setCollection:() => dispatch(fetchCollectionsStartAsync())
})


export default connect(null,mapDispatchToProps)(ShopPage);
