import React, { useState } from 'react';
import { StyledWrapper,FormInput,FormSubmit,FormTitle,FormSubTitle,SignInForm,FormLabel } from './styles';
import Input from '../formInput/formInput'
import CustomButton from '../customButton/customButton'

const SignIn = () => {
	const [ state, setState ] = useState({
		email: '',
		password: ''
	});

	const handleSubmit = (e) => {
		console.log(state);
		e.preventDefault();
		setState({
			email: '',
			password: ''
		});
	};

	const handleChange = (e) => {
		setState({
			...state,
			[e.target.name]: e.target.value
		});
	};

	return (
		<StyledWrapper>
			<FormTitle>I already have an account</FormTitle>
			<FormSubTitle>Sign in with your email and password</FormSubTitle>

			<SignInForm onSubmit={handleSubmit}>
        <Input 
          name="email" 
          type="email" 
          value={state.email} 
          handleChange={handleChange} 
          label="email"/>
        <Input 
          name="password" 
          type="password" 
          wider 
          value={state.password} 
          handleChange={handleChange} 
          label="password"/>
				<CustomButton type="submit">Submit Form</CustomButton>
			</SignInForm>
		</StyledWrapper>
	);
};

export default SignIn;
