import React from 'react'
import {StyledWrapper,ItemCount,ShoppingIcon} from './styles.js'
import {connect} from 'react-redux';
import {setVisibilityAction} from '../../redux/cart/cartActions.js'

const CartIcon = ({setCartVisible,items}) => {

  const sumOfItems = items.reduce((a,b) => a.quantity + b.quantity)

  return (
    <StyledWrapper onClick={() => setCartVisible()}>
      <ShoppingIcon />
      <ItemCount>{sumOfItems}</ItemCount>
    </StyledWrapper>
  )
}

const mapDispatchToProps = (dispatch) => {
  return{
    setCartVisible:() => dispatch(setVisibilityAction())
  }
}


const mapStateToProps = (state) => {
  return {
    items: state.cartReducer.cartItems
  }
} 

export default connect(mapStateToProps,mapDispatchToProps)(CartIcon);
