import {SHOP_DATA} from '../../pages/shoppage/shop.data'

export const addItemToCart = (cartItems,cartItemToAdd) => {
    // find existed item
    const existedItem = cartItems.find(cartItem => cartItem.id === cartItemToAdd.id);

    // if exist loop cartItems, 
    //return array 
    // find item and when found return update item else return item
    if(existedItem){
        return cartItems.map(item => 
            item.id === cartItemToAdd.id ?
            {...item,quantity:item.quantity + 1} : item
            )
    }else{
        return [...cartItems, {...cartItemToAdd, quantity:1}]
    }
}

export const removeItemFromCart = (cartItems, cartItemToRemove) => {
    return [...cartItems.filter(item => item.id !== cartItemToRemove.id)]
}


export const removeItemQuantity = (cartItems,cartItemToIncrease) => {
   return cartItems.map(item => {
        if(item.id === cartItemToIncrease.id && item.quantity > 0){
            return {
                ...item,
                quantity:item.quantity - 1
            }
        }else if(item.quantity === 0){
            cartItems.filter(item => item.id !== cartItemToIncrease.id)
        }
        return item;
    })
}
