import {put,takeLatest,all,call} from 'redux-saga/effects'
import actionType from './userActionTypes'
import {auth,googleProvider,createUserProfileDocument} from '../../firebase/firebase.utils'
import {googleSignInSuccess, googleSignInFailure, emailSignInSuccess, emailSignInFailure} from './userActions';





// google sign in
export function* signInWithGoogle(){
    try{
        const {user} = yield auth.signInWithPopup(googleProvider);
        const userRef = yield call(createUserProfileDocument, user);
        const userSnapshot = yield userRef.get();
        yield put(googleSignInSuccess({
            id:userSnapshot.id,
            ...userSnapshot.data()
        }))
    }catch(err){
        yield put(googleSignInFailure(err))
    }
}

export function* onGoogleSignInStart(){
    yield takeLatest(actionType.GOOGLE_SIGN_IN_START,signInWithGoogle);
}



// email sign in
// payload comes from action: actionType.EMAIL_SIGN_IN_START
export function* signInWithEmail(payload){
    const {email,password} = payload.payload;
    try{
        const {user} = yield auth.signInWithEmailAndPassword(email,password);
        const userRef = yield call(createUserProfileDocument,user);
        const userSnapshot = yield userRef.get();
        yield put(
            emailSignInSuccess({id:userSnapshot.id,...userSnapshot.data()})
        )
    }catch(err){
        yield put(emailSignInFailure(err))
    }
}


export function* onEmailSignInStart(){
    yield takeLatest(actionType.EMAIL_SIGN_IN_START, signInWithEmail)
}


