import {createSelector} from 'reselect'

const selectUser = state => state.userReducer;

// selectUser = {
    // userReducer:{
    //     currentUser:..
    // }
//}


export const selectCurrentUser = createSelector(
    selectUser,
    (user) => user.currentUser
)



const exampleState = {
    shop:{
        taxPercent:9,
        items:[
            {name:'apple',value:1.20},
            {name:'orange', value:2.65}
        ]
    }
}

const selectItems = state => state.shop


console.log(selectItems(exampleState))
